import React, {Component} from 'react';
import FormInput from "../form-input/form-input.component";
import {SignUpWrapper, SignUpTitle, SignUpBtn} from "./sign-up.style";
import {PTag} from "../sign-in/sign-in.style";
import {auth, createUserProfileDocument} from "../../firebase/firebase.utils";

class SignUp extends Component {
  constructor(props) {
    super(props);

    this.state = {
      displayName: '',
      email: '',
      password: '',
      confirmPassword: ''
    }
  }

  handleSubmit = async e => {
    e.preventDefault()

    const {displayName, email, password, confirmPassword} = this.state
    if (password !== confirmPassword) {
      alert("password don't match")
      return
    }
    try {
      const {user} = await auth.createUserWithEmailAndPassword(
        email,
        password
      )

      await createUserProfileDocument(user, {displayName});
      this.setState({
        displayName: '',
        email: '',
        password: '',
        confirmPassword: ''
      });
    } catch (error) {
      console.error(error);
    }
  }

  handleChange = e => {
    const {name, value} = e.target
    this.setState({[name]: value})
  }

  render() {
    const {displayName, email, password, confirmPassword} = this.state
    return (
      <SignUpWrapper>
        <SignUpTitle className='title'> I do not have a account</SignUpTitle>
        <PTag>Sign Up with your email and password.</PTag>
        <form className='sign-up-form' onSubmit={this.handleSubmit}>
          <FormInput type='text'
                     name='displayName'
                     value={displayName}
                     onChange={this.handleChange}
                     label='Display Name' required/>

          <FormInput type='email'
                     name='email'
                     value={email}
                     onChange={this.handleChange}
                     label='Email' required/>

          <FormInput type='password'
                     name='password'
                     value={password}
                     onChange={this.handleChange}
                     label='Password' required/>

          <FormInput type='password'
                     name='confirmPassword'
                     value={confirmPassword}
                     onChange={this.handleChange}
                     label='Confirm Password' required/>
          <SignUpBtn type='submit'>SIGN UP</SignUpBtn>
        </form>
      </SignUpWrapper>
    )
  }
}

export default SignUp;
