import styled from 'styled-components';
import {fontColor} from "../header/header.style";

export const Wrapper = styled.div`
  width: 45px;
  height: 45px;
  position: relative;
  display: flex;
  align-items: center;
  justify-content: center;
  cursor: pointer;
  ${fontColor};

  .shopping-icon {
    width: 40px;
    height: 40px;
    position: relative;
    fill: #915103;
  }

  .item-count {
    font-size: 10px;
    font-weight: bold;
    bottom: 17px;
    right: 18px;
    position: absolute;
    ${fontColor};

    @media screen and (max-width: 800px) {
      font-size: 10px;
      left: 50%;
      top: 50%;
      transform: translate(-50%, -50%);
      position: absolute;
    }
  }
`
